mapper.update:
update sys_user
dept_id = #{deptId}, factory_id = #{factoryId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, birthday = #{birthday}, id_card = #{idCard}, family_address = #{familyAddress}, car_id = #{carId}, car_card = #{carCard}, face = #{face}, emp_no = #{empNo}, position_card_no = #{positionCardNo}, sn_num = #{snNum}, join_date = #{joinDate}, sended = #{sended}, emis_standard = #{emisStandard}, emis_standard_name = #{emisStandardName}, env_sign = #{envSign}, display_status = #{displayStatus}, plc = #{plc}, update_time = getdate()
where user_id = #{userId}

update sys_user
set status = #{status}
where user_id = #{userId}

update sys_user
set avatar = #{avatar}
where user_name = #{userName}

update sys_user
set password = #{password}
where user_name = #{userName}

update sys_user
set sended = #{sendedStatus}
where user_id = #{userId}

update sys_user
set factory_id = #{factoryId},
    plc        = #{plc}
where dept_id = #{deptId}

update.foreach:
update sys_user
id_card= #{item.idCard}, user_name= #{item.userName}, nick_name = #{item.nickName}, phonenumber = #{item.phonenumber}, sex = #{item.sex}, family_address = #{item.familyAddress}, face = #{item.face}, sended=#{item.sended}, update_by = #{item.updateBy}, update_time = #{item.updateTime}
where user_id = #{item.userId}
